import React from "react";

const fotos = [
  "/photos/foto1.jpg",
  "/photos/foto2.jpg",
  "/photos/foto3.jpg",
  "/photos/foto4.jpg",
];

const App = () => {
  return (
    <div style={styles.container}>
      <h1 style={styles.title}>Alan & Vitória</h1>
      <h2 style={styles.subtitle}>4 Anos de Amor ❤️</h2>

      <div style={styles.gallery}>
        {fotos.map((url, i) => (
          <div key={i} style={styles.photoContainer}>
            <img src={url} alt={`Foto ${i + 1}`} style={styles.photo} />
          </div>
        ))}
      </div>

      <div style={styles.playerWrapper}>
        <iframe
          width="100%"
          height="166"
          scrolling="no"
          frameBorder="no"
          allow="autoplay"
          src="https://w.soundcloud.com/player/?url=https%3A//soundcloud.com/kaweananda/portugal&color=%23ff0066&auto_play=true&hide_related=false&show_comments=true&show_user=true&show_reposts=false&show_teaser=true&visual=true"
          title="SoundCloud Player"
        ></iframe>
      </div>
    </div>
  );
};

const styles = {
  container: {
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
    background: "linear-gradient(135deg, #ffdde1 0%, #ee9ca7 100%)",
    minHeight: "100vh",
    padding: "40px 20px",
    textAlign: "center",
    color: "#6b0030",
    boxSizing: "border-box",
    position: "relative",
  },
  title: {
    fontSize: "3rem",
    marginBottom: 8,
    fontWeight: "800",
    textShadow: "2px 2px 4px rgba(255,255,255,0.6)",
  },
  subtitle: {
    fontSize: "1.5rem",
    marginBottom: 30,
    fontWeight: "600",
    fontStyle: "italic",
  },
  gallery: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(280px, 1fr))",
    gap: 24,
    maxWidth: 1200,
    margin: "0 auto",
  },
  photoContainer: {
    borderRadius: 15,
    overflow: "hidden",
    boxShadow: "0 8px 20px rgba(107, 0, 48, 0.3)",
    transition: "transform 0.3s ease",
    cursor: "pointer",
  },
  photo: {
    width: "100%",
    height: 200,
    objectFit: "cover",
    display: "block",
    borderRadius: 15,
  },
  playerWrapper: {
    marginTop: 40,
    maxWidth: 800,
    marginLeft: "auto",
    marginRight: "auto",
  },
};

export default App;
